import React, { useEffect, useState, useRef } from 'react';
import Card from './Card';
import Navbar from './Navbar';
import FileUpload from './FileUpload';
import supabase from './supabaseClient';

export default function Catalog({ user, setUser }) {
    const [cards, setCards] = useState([]);
    const [product, setProduct] = useState({
        product_url: "",
        product_title: "",
        price_available: "",    
        currency: "",    
        price: "",    
        brand: "",    
        image: "",    
        description: "",    
        quantity: "",    
        users: "",    
        additional_description: "",    
        additional_images: "",
        email: `${user}`,
    });
    const [editProduct, setEditProduct] = useState(null);
    const [showsetcatalog, setshowsetcatalog] = useState(false);
    const [dragging, setDragging] = useState(false);
    const [imagePreview, setImagePreview] = useState(null);
    const cardGridRef = useRef(null);
    const dropAreaRef = useRef(null);
    const fileInputRef = useRef(null);
    const pagebtnRef = useRef(null);
    const [currentPage, setCurrentPage] = useState(1);
    const [cardsPerPage] = useState(40);

    function handleChange(event) {
        const { name, value } = event.target;
        setProduct(prevFormData => ({
            ...prevFormData,
            [name]: value
        }));
    }

    async function addProduct() {
        const { data, error } = await supabase
            .from('user_data')
            .insert(product)
            .select();
        if (error) {
            console.error('Error inserting data:', error);
        } else {
            console.log('Product added successfully:', data[0]);
            setCards(prevCards => [
                ...prevCards,
                <Card key={data[0].id} obj={data[0]} setCards={setCards} editCard={editCard} />
            ]);
            crossproduct();
        }
    }

    async function editCard(cardData) {
        setEditProduct(cardData);
        setProduct(cardData);
        setImagePreview(cardData.image);
        setshowsetcatalog(true);
    }

    async function saveEditProduct() {
        const { data, error } = await supabase
            .from('user_data')
            .update(product)
            .eq('id', product.id)
            .select();
        if (error) {
            console.error('Error updating data:', error);
        } else {
            console.log('Product updated successfully:', data[0]);
            setCards(prevCards =>
                prevCards.map(card =>
                    card.props.obj.id === product.id
                        ? <Card key={data[0].id} obj={data[0]} setCards={setCards} editCard={editCard} />
                        : card
                )
            );
            crossproduct();
        }
    }

    function crossproduct() {
        setProduct({
            product_url: "",
            product_title: "",
            price_available: "",    
            currency: "",    
            price: "",    
            brand: "",    
            image: "",    
            description: "",    
            quantity: "",    
            users: "",    
            additional_description: "",    
            additional_images: "",
            email: `${user}`,
        });
        setEditProduct(null);
        setshowsetcatalog(false);
        setImagePreview(null);
    }

    useEffect(() => {
        async function fetchUserData() {
            const { data, error } = await supabase
                .from('user_data')
                .select('*')
                .eq('email', user);

            if (error) {
                console.error('Error fetching data:', error);
            } else {
                setCards(data.map(obj => <Card key={obj.id} obj={obj} setCards={setCards} editCard={editCard} />));
            }
        }
        if (user) {
            fetchUserData();
        }
    }, [user]);

    useEffect(() => {
        if (showsetcatalog) {
            const catalogHeight = document.querySelector('.set-catalog').offsetHeight;
            cardGridRef.current.style.top = `${160 + catalogHeight}px`;
        } else {
            cardGridRef.current.style.top = '200px';
        }
    }, [showsetcatalog]);

    useEffect(() => {
        if (cards.length > 0) {
            const rowHeight = document.querySelector('.card-grid').offsetHeight;
            if (pagebtnRef.current) {
                pagebtnRef.current.style.top = `${300 + rowHeight}px`;
            }
        }
    }, [cards, currentPage]);

    useEffect(() => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
    }, [currentPage]);

    const handleDragOver = (e) => {
        e.preventDefault();
        setDragging(true);
    };

    const handleDragLeave = () => {
        setDragging(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setDragging(false);
        const file = e.dataTransfer.files[0];
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onload = () => {
                setImagePreview(reader.result);
                setProduct(prevFormData => ({
                    ...prevFormData,
                    image: reader.result
                }));
            };
            reader.readAsDataURL(file);
        }
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onload = () => {
                setImagePreview(reader.result);
                setProduct(prevFormData => ({
                    ...prevFormData,
                    image: reader.result
                }));
            };
            reader.readAsDataURL(file);
        }
    };

    const handleButtonClick = () => {
        fileInputRef.current.click();
    };

    const indexOfLastCard = currentPage * cardsPerPage;
    const indexOfFirstCard = indexOfLastCard - cardsPerPage;
    const currentCards = cards.slice(indexOfFirstCard, indexOfLastCard);

    const paginate = pageNumber => setCurrentPage(pageNumber);

    const totalPages = Math.ceil(cards.length / cardsPerPage);

    return (
        <div className='catalog'>
            <Navbar setUser={setUser} />
            {showsetcatalog && (
                <div className='set-catalog'>
                    <h2>Product Information</h2>
                    <button className='tick-btn' onClick={editProduct ? saveEditProduct : addProduct}>
                        {editProduct ? "Save" : "Add"}
                    </button>
                    <button className='cross-btn' onClick={crossproduct}>X</button>
                    <div
                        className={`drop-area ${dragging ? 'drag-over' : ''}`}
                        onDragOver={handleDragOver}
                        onDragLeave={handleDragLeave}
                        onDrop={handleDrop}
                        ref={dropAreaRef}
                    >
                        {imagePreview ? (
                            <img src={imagePreview} alt="Preview" />
                        ) : (
                            <>
                                <p>Drag & drop an image here or click to select</p>
                                <button type="button" onClick={handleButtonClick}>Select Image</button>
                                <input
                                    type="file"
                                    ref={fileInputRef}
                                    onChange={handleFileChange}
                                    accept="image/*"
                                />
                            </>
                        )}
                    </div>
                    <div className='product-grid'>
                        <div className="label-container">
                            <span className='addproduct-span'>Product URL</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="product_url"
                                value={product.product_url}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Product Title</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="product_title"
                                value={product.product_title}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Price Available</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="price_available"
                                value={product.price_available}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Currency</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="currency"
                                value={product.currency}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Price</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="price"
                                value={product.price}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Brand</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="brand"
                                value={product.brand}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Description</span>
                            <textarea
                                onChange={handleChange}
                                name="description"
                                value={product.description}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Quantity</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="quantity"
                                value={product.quantity}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Users</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="users"
                                value={product.users}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Additional Description</span>
                            <textarea
                                onChange={handleChange}
                                name="additional_description"
                                value={product.additional_description}
                            />
                        </div>
                        <div className="label-container">
                            <span className='addproduct-span'>Additional Images</span>
                            <input
                                type="text"
                                onChange={handleChange}
                                name="additional_images"
                                value={product.additional_images}
                            />
                        </div>
                    </div>
                </div>
            )}
            <div className='card-grid' ref={cardGridRef}>
                {currentCards}
            </div>
            {totalPages > 1 && (
                <div className='page-btns' ref={pagebtnRef}>
                    {Array.from({ length: totalPages }, (_, index) => (
                        <button
                            key={index + 1}
                            onClick={() => paginate(index + 1)}
                            className={currentPage === index + 1 ? 'active' : ''}
                        >
                            {index + 1}
                        </button>
                    ))}
                </div>
            )}
            <FileUpload user={user}/>
        </div>
    );
}
