import React from "react";
import Card from "./Card";
import data from "./data";

export default function Catalog(){

    const[myData, setMyData] = React.useState(data)
    let myarr= myData.map(obj => {
        return (<Card  key={obj.id} obj={obj} />)
    })

    

    function cardCall(categoryno){
        myarr= myData.map(obj => {
            return (<Card  key={obj.id} obj={obj} />)
        })
    }


    let categoryno = 1
    return(
        <div>
            <form className="category-form">
                <select>
                    <option onClick={() => {cardCall(categoryno)}}>Category {categoryno++}</option>
                    <option onClick={() => {cardCall(categoryno)}}>Category {categoryno++}</option>
                    <option onClick={() => {cardCall(categoryno)}}>Category {categoryno++}</option>
                    <option onClick={() => {cardCall(categoryno)}}>Category {categoryno++}</option>
                </select>
            </form>
            <div className="card-grid">
            {myarr}
            </div>
        </div>
    )
}
