import React from "react"
import './login.css'
import { Link } from "react-router-dom";
import { useGoogleLogin } from '@react-oauth/google';
import { useNavigate } from "react-router-dom";
import { createClient } from "@supabase/supabase-js";
import { Auth } from "@supabase/auth-ui-react";
import supabase from './supabaseClient';





function Login(props){

    let userexist = false

    const navigate = useNavigate();

    const googlesignin = useGoogleLogin({
        onSuccess: tokenResponse => {
            console.log(tokenResponse);
            navigate("/app1"); // Navigate to /app1 upon successful login
        },
      });

      const [formData, setFormData] = React.useState(
        {   
            name: "", 
            email: "",
            password: ""
        }
    )

    const[signinData,setSigninData] = React.useState({
        name: "",
        email: ""
    })


    function handleChange(event) {
        const {name, value} = event.target
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [name]: value
            }
        })
    }

    async function createUser(){
        try {
            const {data, error} = await supabase
            .from("profiles")
            .insert({
                name: formData.name,
                email: formData.email,
                password: formData.password
            })
            .single()
            if(error) throw error;
            else{
                props.setUser(formData)
                //window.location.reload();
                navigate("/app");
            }
            
        } catch (error) {
            alert(error.message);
        }
    }

    async function getUsers(){
        try {
            const {data,error} = await supabase
            .from("profiles")
            .select("*")
            if(error) throw error
            if(data !== null){
                console.log(data)
            }

        } catch (error) {
            alert(error.message)
        }
    }

    
    async function compareUser(){
        try {
            const {data,error} = await supabase
            .from("profiles")
            .select("email")
            .eq("email",formData.email)
            if(error) throw error
            if(data !== null){
                userexist=true;
            }

        } catch (error) {
            alert(error.message)
        }
    }

    function signingin(){
        if(userexist){
            navigate("/app")
        }else{
            console.log("Please sign up first")
        }
    }

    React.useEffect(()=>{
        getUsers();
        compareUser();
    },[])


    React.useEffect(() => {
        const signupButton = document.querySelector('.img__btn');
        const container = document.querySelector('.cont');
        
        const toggleSignupClass = () => {
            container.classList.toggle('s--signup');
        };

        if (signupButton) {
            signupButton.addEventListener('click', toggleSignupClass);
        }

        // Cleanup event listener on component unmount
        return () => {
            if (signupButton) {
                signupButton.removeEventListener('click', toggleSignupClass);
            }
        };
    }, []);

    return(
        <div className="cont">
        <div className="form sign-in">
        <h2>Welcome !</h2>
            <label>
                <span>Email</span>
                <input 
                type="email"
                    
                />
            </label>
            <label>
                <span>Password</span>
                <input type="password" />
            </label>
            <p className="forgot-pass">Forgot password?</p>
            <Link to="/app" className="signin-link">
            <button 
            type="button" 
            className="submit"  
            onClick={signingin}>
            Sign In
            </button>
            </Link> 
            <p className="OR">-------------OR-------------</p>
            <button className="submit-google" onClick={googlesignin}>sign in with google </button>
        </div>

        <div className="sub-cont">
            <div className="img">
                <div className="img__text m--up">
                    <h2>Don't have an account? </h2>
                    <h2> Please Sign up!</h2>
                </div>
                <div className="img__text m--in">
                
                    <h2>If you already has an account, </h2>
                    <h2> just sign in.</h2>
                </div>
                <div className="img__btn">
                    <span className="m--up">Sign Up</span>
                    <span className="m--in"> <button>Sign In</button></span>
                </div>
            </div>
            <div className="form sign-up">
                <h2>Create your Account</h2>
                <label>                
                    <span>Name</span>
                    <input
                    type="text"
                    placeholder="Name"
                    onChange={handleChange}
                    name="name"
                    value={formData.name}
                    />
                </label>
                <label>                
                    <span>Email</span>
                    <input
                    type="email"
                    placeholder="Email"
                    onChange={handleChange}
                    name="email"
                    value={formData.email}
                    />
                </label>
                <label>
                    <span>Password</span>
                    <input type="password"
                    placeholder="Password"
                    onChange={handleChange}
                    name="password"
                    value={formData.password}
                    />
                </label>
                <button type="button" className="submit" onClick={createUser}>Sign Up</button>
                
            </div>
        </div>
    </div>
    )
} 
            
export default Login;

