import React, { useState } from 'react';
import Papa from 'papaparse';
import supabase from './supabaseClient';
import Card from './Card';

export default function FileUpload({ setCards, user }) {
    const handleFileUpload = async (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();

        reader.onload = async (e) => {
            const text = e.target.result;
            const { data, errors } = Papa.parse(text, {
                header: true, // Treat first row as headers
                skipEmptyLines: true // Skip empty lines
            });

            if (errors.length > 0) {
                console.error('CSV parsing error:', errors);
                return;
            }

            // Add the email field to each record
            const dataWithEmail = data.map(record => ({
                ...record,
                email: user.email
            }));

            // Uploading to Supabase
            const { error } = await supabase
                .from('user_data')
                .insert(dataWithEmail);

            if (error) {
                console.error('Error inserting data:', error);
            } else {
                // Update the state with new cards
                setCards(dataWithEmail.map((obj, index) => <Card key={index} obj={obj} />));
            }
        };

        reader.readAsText(file);
    };

    return (
        <div>
            <input
                type="file"
                accept=".csv"
                id="csvFileInput"
                onChange={handleFileUpload}
                style={{ display: 'none' }}
            />
            <label htmlFor="csvFileInput" className="labelStyle">+</label>
        </div>
    );
}
