import React, { useState } from 'react';
import Papa from 'papaparse';

const labelStyle = {
    display: 'inline-block',
    width: '40px',
    height: '40px',
    lineHeight: '40px',
    borderRadius: '50%',
    backgroundColor: '#007bff',
    color: 'white',
    textAlign: 'center',
    cursor: 'pointer',
    fontSize: '24px',
    fontWeight: 'bold'
};

function FileUpload() {
    const [csvData, setCsvData] = useState([]);

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            Papa.parse(file, {
                header: true,
                skipEmptyLines: true,
                complete: function (results) {
                    setCsvData(results.data);
                    console.log(results.data); // You can remove this line in production
                },
                error: function (error) {
                    console.error('Error parsing CSV file:', error);
                }
            });
        }
    };

    return (
        <div>
            <input
                type="file"
                accept=".csv"
                id="csvFileInput"
                onChange={handleFileUpload}
                style={{ display: 'none' }}
            />
            <label htmlFor="csvFileInput" style={labelStyle}>
                +
            </label>
            {csvData.length > 0 && (
                <table>
                    <thead>
                        <tr>
                            {Object.keys(csvData[0]).map((key) => (
                                <th key={key}>{key}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {csvData.map((row, index) => (
                            <tr key={index}>
                                {Object.values(row).map((value, idx) => (
                                    <td key={idx}>{value}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}



export default FileUpload;
