import React from "react";
import Card from "./Card";
import data from "./data";

export default function Catalog(){

    const[myData, setMyData] = React.useState(data)

    const[cards, setCards] = React.useState((<> </>))
    let categoryno = 1

    function cardCall(event){
        let index= event.target.value
        
        let myarr= myData[index].map(obj => {
            return (<Card  key={obj.id} obj={obj} />)
        })

        setCards(myarr)
    }

   
    return(
        <div>
            <form className="category-form">
                <select onChange={ cardCall}>
                    <option value={0}>--Choose an option--</option>
                    <option value={1}>Category {categoryno+0}</option>
                    <option value={2}>Category {categoryno+1}</option>
                    <option value={3}>Category {categoryno+2}</option>
                    <option value={4}>Category {categoryno+3}</option>
                </select>
            </form>

            <div className="card-grid">
            {cards}
            </div>
        </div>
    )
}
