import React from "react"
import './login.css'
import { Link } from "react-router-dom";
import { useGoogleLogin } from '@react-oauth/google';
import { useNavigate } from "react-router-dom";
import supabase from './supabaseClient';
import axios from 'axios';






function Login(props){

    const navigate = useNavigate();

    const googlesignin = useGoogleLogin({
        onSuccess: async tokenResponse => {
            console.log(tokenResponse);

            // Fetch user info from Google API
            try {
                const userInfoResponse = await axios.get(
                    'https://www.googleapis.com/oauth2/v1/userinfo?alt=json',
                    {
                        headers: {
                            Authorization: `Bearer ${tokenResponse.access_token}`,
                        },
                    }
                );

                const { email, name } = userInfoResponse.data;
                console.log("User info:", userInfoResponse.data);

                // Here you can handle the user info, e.g., check if the user exists in your database,
                // create a new user, and set the user info in your application state
                // For example:
                const { data, error } = await supabase
                    .from("profiles")
                    .upsert({
                        email: email,
                        name: name,
                    })
                    .single();

                if (error) throw error;

                props.setUser({ email, name });
                navigate("/app1"); // Navigate to /app1 upon successful login
            } catch (error) {
                console.error("Error fetching user info:", error);
            }
        },
    });

      const [formData, setFormData] = React.useState(
        {   
            name: "", 
            email: "",
            password: ""
        }
    )

    const [signinData, setSigninData] = React.useState({
            email: "",
            password: ""
    })


    function handleChange(event) {
        const {name, value} = event.target
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [name]: value
            }
        })
    }

    function handleChangeSignin(event) {
        const {name, value} = event.target
        setSigninData(prevFormData => {
            return {
                ...prevFormData,
                [name]: value
            }
        })
    }

    async function createUser(){
        try {
            const {data, error} = await supabase
            .from("profiles")
            .select("*")
            .eq("email", formData.email)
            if(error) throw error;
            if(data === null){
                const {data, error} = await supabase
                .from("profiles")
                .insert({
                    name: formData.name,
                    email: formData.email,
                    password: formData.password
                })
                .single()
                props.setUser(formData)
                //window.location.reload();
                navigate("/app");
            }else{
                console.log("user already exists. Please login")
            } 
        } catch (error) {
            alert(error.message);
        }
    }

    
    async function signingin(){
        try {
            const {data,error} = await supabase
            .from("profiles")
            .select("*")
            .eq("email",signinData.email)
            if(error) throw error
            if (data.length > 0) {  // Check if any user is found
                const user = data[0]; // Get the first user
                if (user.password === signinData.password) {
                    props.setUser(signinData);
                    console.log("Signed in successfully");
                    navigate("/app");
                } else {
                    console.log("Wrong password");
                }
            } else {
                console.log("Please sign up first");
            }
        } catch (error) {
            alert(error.message);
        }
    }



    React.useEffect(() => {
        const signupButton = document.querySelector('.img__btn');
        const container = document.querySelector('.cont');
        
        const toggleSignupClass = () => {
            container.classList.toggle('s--signup');
        };

        if (signupButton) {
            signupButton.addEventListener('click', toggleSignupClass);
        }

        // Cleanup event listener on component unmount
        return () => {
            if (signupButton) {
                signupButton.removeEventListener('click', toggleSignupClass);
            }
        };
    }, []);

    return(
        <div className="cont">
        <div className="form sign-in">
        <h2>Welcome !</h2>
            <label>
                <span>Email</span>
                <input 
                    type="email"
                    onChange={handleChangeSignin}
                    name="email"
                    value={signinData.email}
                />
            </label>
            <label>
                <span>Password</span>
                <input 
                    type="password"
                    placeholder="********"
                    onChange={handleChangeSignin}
                    name="password"
                    value={signinData.password}
                />
            </label>
            <p className="forgot-pass">Forgot password?</p>
            <button 
            type="button" 
            className="submit"  
            onClick={signingin}
            >
            Sign In
            </button>
            <p className="OR">-------------OR-------------</p>
            <button className="submit-google" onClick={googlesignin}>sign in with google </button>
        </div>

        <div className="sub-cont">
            <div className="img">
                <div className="img__text m--up">
                    <h2>Don't have an account? </h2>
                    <h2> Please Sign up!</h2>
                </div>
                <div className="img__text m--in">
                
                    <h2>If you already has an account, </h2>
                    <h2> just sign in.</h2>
                </div>
                <div className="img__btn">
                    <span className="m--up">Sign Up</span>
                    <span className="m--in"> <button>Sign In</button></span>
                </div>
            </div>
            <div className="form sign-up">
                <h2>Create your Account</h2>
                <label>                
                    <span>Name</span>
                    <input
                    type="text"
                    placeholder="Name"
                    onChange={handleChange}
                    name="name"
                    value={formData.name}
                    />
                </label>
                <label>                
                    <span>Email</span>
                    <input
                    type="email"
                    placeholder="Email"
                    onChange={handleChange}
                    name="email"
                    value={formData.email}
                    />
                </label>
                <label>
                    <span>Password</span>
                    <input type="password"
                    placeholder="Password"
                    onChange={handleChange}
                    name="password"
                    value={formData.password}
                    />
                </label>
                <button 
                type="button" 
                className="submit" 
                onClick={createUser}
                >
                Sign Up
                </button>
                
            </div>
        </div>
    </div>
    )
} 
            
export default Login;

