import React from "react"
import './login.css'
import { Link } from "react-router-dom";



function Login(){

    React.useEffect(() => {
        const signupButton = document.querySelector('.img__btn');
        const container = document.querySelector('.cont');
        
        const toggleSignupClass = () => {
            container.classList.toggle('s--signup');
        };

        if (signupButton) {
            signupButton.addEventListener('click', toggleSignupClass);
        }

        // Cleanup event listener on component unmount
        return () => {
            if (signupButton) {
                signupButton.removeEventListener('click', toggleSignupClass);
            }
        };
    }, []);

    return(
        <div className="cont">
        <div className="form sign-in">
            <h2>Welcome !</h2>
            <label>
                <span>Email</span>
                <input type="email" />
            </label>
            <label>
                <span>Password</span>
                <input type="password" />
            </label>
            <p className="forgot-pass">Forgot password?</p>
            <button type="button" className="submit" >
               <Link to="/app" class>Sign In</Link> 
            </button>
        </div>

        <div className="sub-cont">
            <div className="img">
                <div className="img__text m--up">
                    <h2>Don't have an account? </h2>
                    <h2> Please Sign up!</h2>
                </div>
                <div className="img__text m--in">
                
                    <h2>If you already has an account, </h2>
                    <h2> just sign in.</h2>
                </div>
                <div className="img__btn">
                    <span className="m--up">Sign Up</span>
                    <span className="m--in"> <button>Sign In</button></span>
                </div>
            </div>
            <div className="form sign-up">
                <h2>Create your Account</h2>
                <label>                
                    <span>Name</span>
                    <input type="text" />
                </label>
                <label>                
                    <span>Email</span>
                    <input type="email" />
                </label>
                <label>
                    <span>Password</span>
                    <input type="password" />
                </label>
                <button type="button" className="submit">Sign Up</button>
                
            </div>
        </div>
    </div>
    )
} 

export default Login;



