import React from 'react';
import Card from './Card';
import Navbar from './Navbar'
import FileUpload from './FileUpload';

export default function Catalog({ csvData }) {
    const [cards, setCards] = React.useState(<></>);

    function cardCall(event) {
        let index = event.target.value;
        if (index > 0) {
            let myarr = csvData.map(obj => {
                return <Card key={obj.id} obj={obj} />;
            });
            setCards(myarr);
        } else {
            setCards([]);
        }
    }

    return (
        <div>
        <Navbar/>
        <div className='set-catalog'>
        <FileUpload/>

        </div>
            <form id="form1" className="category-form">
                <select onChange={cardCall}>
                    <option value={0}>--Choose an option--</option>
                    {/* Assuming each category corresponds to a part of csvData */}
                    <option value={1}>Category 1</option>
                    <option value={2}>Category 2</option>
                    <option value={3}>Category 3</option>
                </select>
            </form>
            <div className="card-grid">
                {cards}
            </div>
        </div>
    );
}
